package com.codershop.shoppinganywhere.common.dao.impl;import com.codershop.shoppinganywhere.common.dao.ScheduleConfigDao;import com.codershop.shoppinganywhere.common.entity.ScheduleConfig;import com.codershop.shoppinganywhere.common.generics.impl.GenericDaoImpl;import com.google.common.collect.ImmutableMap;import org.springframework.stereotype.Repository;import java.util.List;/** * com.fis.fw.core.dao.impl.ScheduleConfigDaoImpl * TungHuynh * Created by sondt18@fpt.com.vn * Date 24/06/2019 9:47 AM */@Repository("ScheduleConfigDaoImpl")public class ScheduleConfigDaoImpl extends GenericDaoImpl<ScheduleConfig, Integer> implements ScheduleConfigDao {    @Override    public ScheduleConfig getByCode(String code) {        String sql = "select SCHEDULE_CONFIG_ID adScheduleConfigId, SCHEDULE_CODE scheduleCode, SCHEDULE_NAME scheduleName, PARAMS, STATUS" +                " from SCHEDULE_CONFIG where STATUS='ACTIVE' and SCHEDULE_CODE=:code";        List<ScheduleConfig> lst = getByCondition(ImmutableMap.of("code", code), sql, ScheduleConfig.class);        if (lst != null && !lst.isEmpty()) {            return lst.get(0);        }        return null;    }}